import React, { useState, useEffect } from "react";
import { usePlaidLink } from "react-plaid-link";
import axios from "axios";
import {
  Button,
  CircularProgress,
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";

const PlaidIntegration = () => {
  const [linkToken, setLinkToken] = useState(null);
  const [accessToken, setAccessToken] = useState(null);
  const [accounts, setAccounts] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [income, setIncome] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch link token from backend
  useEffect(() => {
    const fetchLinkToken = async () => {
      try {
        const response = await axios.post("http://localhost:5000/api/create_link_token");
        setLinkToken(response.data.link_token);
      } catch (error) {
        console.error("Error creating link token:", error);
      }
    };
    fetchLinkToken();
  }, []);

  // Initialize Plaid Link
  const { open, ready } = usePlaidLink({
    token: linkToken,
    onSuccess: async (publicToken) => {
      try {
        setLoading(true);
        const response = await axios.post("http://localhost:5000/api/exchange_public_token", { public_token: publicToken });
        setAccessToken(response.data.access_token);
        fetchBankData(response.data.access_token);
      } catch (error) {
        console.error("Error exchanging token:", error);
      } finally {
        setLoading(false);
      }
    },
  });

  // Function to filter out null or undefined fields
  const filterNullValues = (obj) => {
    return Object.fromEntries(Object.entries(obj).filter(([_, value]) => value !== null && value !== undefined));
  };

  // Fetch accounts, transactions, and income data
  const fetchBankData = async (token) => {
    try {
      setLoading(true);

      // Fetch Accounts
      const accountResponse = await axios.post("http://localhost:5000/api/accounts", { access_token: token });
      const displayedAccounts = accountResponse.data.accounts?.map((account) =>
        filterNullValues({
          name: account.name,
          type: account.type,
          balance: account.balances?.current,
        })
      );
      if (displayedAccounts.length > 0) {
        console.log(JSON.stringify({ event: "Accounts Fetched", accounts: displayedAccounts }));
      }
      setAccounts(displayedAccounts || []);

      // Fetch Transactions
      const transactionResponse = await axios.post("http://localhost:5000/api/transactions", { access_token: token });
      const displayedTransactions = transactionResponse.data.transactions?.map((transaction) =>
        filterNullValues({
          name: transaction.name,
          amount: transaction.amount,
          date: transaction.date,
        })
      );
      if (displayedTransactions.length > 0) {
        console.log(JSON.stringify({ event: "Transactions Fetched", transactions: displayedTransactions }));
      }
      setTransactions(displayedTransactions || []);

      // Fetch Income
      const incomeResponse = await axios.post("http://localhost:5000/api/income", { access_token: token });
      const displayedIncome = incomeResponse.data.income?.map((incomeSource) =>
        filterNullValues({
          source: incomeSource.source,
          avgMonthlyIncome: incomeSource.avgMonthlyIncome,
        })
      );
      if (displayedIncome.length > 0) {
        console.log(JSON.stringify({ event: "Income Fetched", income: displayedIncome }));
      }
      setIncome(displayedIncome || []);

    } catch (error) {
      console.error("❌ Error fetching financial data:", error);
    } finally {
      setLoading(false);
    }
  };

  // Helper function to group transactions by month and calculate total spending
  const groupTransactionsByMonth = () => {
    const grouped = {};

    transactions.forEach((transaction) => {
      if (!transaction.date || !transaction.amount) return;

      const date = new Date(transaction.date);
      if (isNaN(date)) return;

      const monthYear = date.toLocaleString("default", { month: "short", year: "numeric" });

      if (!grouped[monthYear]) {
        grouped[monthYear] = { transactions: [], total: 0 };
      }

      grouped[monthYear].transactions.push(transaction);
      grouped[monthYear].total += Number(transaction.amount);
    });

    return grouped;
  };

  const groupedTransactions = groupTransactionsByMonth();

  return (
    <Container maxWidth="md" style={{ textAlign: "center", marginTop: "20px" }}>
      <Typography variant="h4" gutterBottom>
        Finance Advisor
      </Typography>

      <Button
        variant="contained"
        color="primary"
        onClick={open}
        disabled={!ready || !linkToken}
        style={{ marginBottom: "20px" }}
      >
        Connect Bank Account
      </Button>

      {loading && <CircularProgress />}

      {/* Display Income Data */}
      {income.length > 0 && (
        <Card style={{ marginTop: "20px", padding: "20px", backgroundColor: "#e8f5e9" }}>
          <CardContent>
            <Typography variant="h5" style={{ fontWeight: "bold", color: "#2E7D32" }}>
              Estimated Monthly Income
            </Typography>
            <List>
              {income.map((incomeSource, index) => (
                <ListItem key={index}>
                  <ListItemText
                    primary={incomeSource.source || "Unknown Source"}
                    secondary={`Estimated Income: $${(incomeSource.avgMonthlyIncome || 0).toFixed(2)}`}
                  />
                </ListItem>
              ))}
            </List>
          </CardContent>
        </Card>
      )}

      {/* Display grouped transactions */}
      {Object.entries(groupedTransactions).map(([month, data]) => (
        <Accordion key={month} style={{ marginTop: "10px", backgroundColor: "#2e2e2e", color: "white" }}>
          <AccordionSummary expandIcon={<ExpandMoreIcon style={{ color: "white" }} />}>
            <Typography variant="h6" style={{ fontWeight: "bold", width: "100%" }}>
              {month} <span style={{ float: "right" }}>${data.total.toFixed(2)}</span>
            </Typography>
          </AccordionSummary>
          <AccordionDetails>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell style={{ color: "white", fontWeight: "bold" }}>Name</TableCell>
                    <TableCell style={{ color: "white", fontWeight: "bold" }}>Amount</TableCell>
                    <TableCell style={{ color: "white", fontWeight: "bold" }}>Date</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {data.transactions.map((transaction, index) => (
                    <TableRow key={index}>
                      <TableCell style={{ color: "white" }}>{transaction.name || "Unknown Transaction"}</TableCell>
                      <TableCell style={{ color: "white" }}>${(transaction.amount || 0).toFixed(2)}</TableCell>
                      <TableCell style={{ color: "white" }}>{transaction.date ? new Date(transaction.date).toDateString() : "Invalid Date"}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </AccordionDetails>
        </Accordion>
      ))}
    </Container>
  );
};

export default PlaidIntegration;
