import React, { useState, useEffect } from "react";
import { usePlaidLink } from "react-plaid-link";
import axios from "axios";
import { Button, CircularProgress, Container, Card, CardContent, Typography, List, ListItem, ListItemText } from "@mui/material";

const PlaidIntegration = () => {
  const [linkToken, setLinkToken] = useState(null);
  const [accessToken, setAccessToken] = useState(null);
  const [accounts, setAccounts] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch link token from backend
  useEffect(() => {
    const fetchLinkToken = async () => {
      try {
        const response = await axios.post("http://localhost:5000/api/create_link_token");
        setLinkToken(response.data.link_token);
      } catch (error) {
        console.error("Error creating link token:", error);
      }
    };
    fetchLinkToken();
  }, []);

  // Initialize Plaid Link
  const { open, ready } = usePlaidLink({
    token: linkToken,
    onSuccess: async (publicToken) => {
      try {
        setLoading(true);
        const response = await axios.post("http://localhost:5000/api/exchange_public_token", { public_token: publicToken });
        setAccessToken(response.data.access_token);
        fetchBankData(response.data.access_token);
      } catch (error) {
        console.error("Error exchanging token:", error);
      } finally {
        setLoading(false);
      }
    },
  });

  // Fetch accounts and transactions
  const fetchBankData = async (token) => {
    try {
      setLoading(true);
  
      // Fetch Accounts
      const accountResponse = await axios.post("http://localhost:5000/api/accounts", { access_token: token });
      console.log("✅ Accounts Response:", accountResponse.data);
      setAccounts(accountResponse.data.accounts || []);
  
      // Fetch Transactions
      const transactionResponse = await axios.post("http://localhost:5000/api/transactions", { access_token: token });
      console.log("✅ Transactions Response:", transactionResponse.data);
      setTransactions(transactionResponse.data.transactions || []);
  
    } catch (error) {
      console.error("❌ Error fetching financial data:", error);
    } finally {
      setLoading(false);
    }
  };
  
  

  return (
    <Container maxWidth="md" style={{ textAlign: "center", marginTop: "20px" }}>
      <Typography variant="h4" gutterBottom>
        Finance Advisor
      </Typography>
      <Button variant="contained" color="primary" onClick={open} disabled={!ready || !linkToken} style={{ marginBottom: "20px" }}>
        Connect Bank Account
      </Button>
      <br/>

      {loading && <CircularProgress />}

      {/* Display accounts */}
      {accounts.length > 0 && (
        <Card style={{ marginTop: "20px", padding: "20px" }}>
          <CardContent>
            <Typography variant="h5">Bank Accounts</Typography>
            <List>
              {accounts.map((account) => (
                <ListItem key={account.account_id}>
                  <ListItemText primary={account.name} secondary={`Balance: $${account.balances.current}`} />
                </ListItem>
              ))}
            </List>
          </CardContent>
        </Card>
      )}

      {/* Display transactions */}
      {transactions.length > 0 && (
        <Card style={{ marginTop: "20px", padding: "20px" }}>
          <CardContent>
            <Typography variant="h5">Recent Transactions</Typography>
            <List>
              {transactions.slice(0, 5).map((transaction) => (
                <ListItem key={transaction.transaction_id}>
                  <ListItemText primary={transaction.name} secondary={`Amount: $${transaction.amount} • ${transaction.date}`} />
                </ListItem>
              ))}
            </List>
          </CardContent>
        </Card>
      )}
    </Container>
  );
};

export default PlaidIntegration;
